mapper.select:
SELECT CAST(id AS CHAR) AS id
FROM tb_customer
WHERE `status` != 2 AND open_id = #{openId}

SELECT COUNT(id) AS ct
FROM tb_customer
WHERE 1=1

    AND open_id = #{openId}


    AND id = #{customerId}

SELECT CAST(id AS CHAR) AS id
FROM tb_customer
WHERE open_id = #{openId}

SELECT open_id AS openId
FROM tb_customer
WHERE id = #{customerId}
