mapper.select:
SELECT COUNT(id) AS ct
FROM tb_driver
WHERE 1=1

    AND open_id = #{openId}


    AND id = #{driverId}

SELECT CAST(id AS CHAR) AS id
FROM tb_driver
WHERE open_id = #{openId}

SELECT name, sex
FROM tb_driver
WHERE id = #{driverId}

mapper.update:
update tb_driver
set archive = 1
WHERE id = #{driverId}

mapper.select:
SELECT CAST(id AS CHAR) AS id,
IFNULL(`name`,"") AS `name`,
IFNULL(sex,"") AS sex,
IFNULL(pid,"") AS pid,
IFNULL(tel,"") AS tel,
IFNULL(contact_name,"") AS contactName,
IFNULL(contact_tel,"") AS contactTel,
IFNULL(real_auth,"") AS realAuth,
`status`,
DATE_FORMAT(create_time, '%Y-%m-%d') AS createTime
FROM tb_driver
WHERE 1=1

    AND `name` = #{name}


    AND tel = #{tel}


    AND pid = #{pid}


    AND sex = #{sex}


    AND `real_auth` = #{realAuth}


    AND `status` = #{status}

LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_driver
WHERE 1=1

    AND `name` = #{name}


    AND tel = #{tel}


    AND pid = #{pid}


    AND sex = #{sex}


    AND `real_auth` = #{realAuth}


    AND `status` = #{status}
