mapper.select:
SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE uuid = #{uuid}

SELECT status
FROM tb_order
WHERE id = #{orderId}

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

mapper.delete:
DELETE FROM tb_order
WHERE id = #{orderId}
AND `status` = 1

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

mapper.select:
SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE customer_id = #{customerId}
  AND `status` IN (2, 3, 4) LIMIT 1;

SELECT
start_place AS startPlace,
start_place_location AS startPlaceLocation,
end_place AS endPlace,
end_place_location AS endPlaceLocation,
`status`
FROM tb_order
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

LIMIT 1;

mapper.update:
UPDATE tb_order
SET

    arrive_time = NOW(),


    start_time = NOW(),
    waiting_minute = CEIL(TIMESTAMPDIFF(SECOND, arrive_time, NOW())/60),


    end_time = NOW(),

`status` = #{status}
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

mapper.select:
SELECT CAST(id AS CHAR) AS id,
CAST(start_place AS CHAR) AS startPlace,
CAST(end_place AS CHAR) AS endPlace,
CAST(real_mileage AS CHAR) AS realMileage,
CAST(real_fee AS CHAR) AS realFee,
`status`,
DATE_FORMAT(create_time, '%Y-%m-%d %H:%i') AS createTime
FROM tb_order
WHERE 1 = 1

    AND id = #{orderId}


    AND customer_id = #{customerId}


    AND driver_id = #{driverId}


    AND date BETWEEN #{startDate} AND #{endDate}


    AND `status` = #{status}

ORDER BY id DESC
LIMIT #{start},#{length}

SELECT COUNT(*)
FROM tb_order
WHERE 1 = 1

    AND id = #{orderId}


    AND customer_id = #{customerId}


    AND driver_id = #{driverId}


    AND date BETWEEN #{startDate} AND #{endDate}


    AND `status` = #{status}

mapper.update:
UPDATE tb_order
SET real_mileage   = #{realMileage},
    return_mileage = #{returnMileage},
    incentive_fee  = #{incentiveFee},
    real_fee       = #{total}
WHERE id = #{orderId}
